import React, {useState, useEffect} from 'react';
import './App.css';
import { Card, Icon} from 'antd';
import Nav from './Nav'
import { connect } from 'react-redux';

const { Meta } = Card;

function ScreenMyArticles(props) {

  const [wishList, setWishList] = useState([]);

  const FakeArticles = props.wishList;

  return (
    <div>
         
            <Nav/>

            <div className="Banner"/>

            <div className="Card">
    

                    <div  style={{display:'flex',justifyContent:'center'}}>
                      <Card
                        style={{  
                          width: 300, 
                          margin:'15px', 
                          display:'flex',
                          flexDirection: 'column',
                          justifyContent:'space-between' }}
                        cover={
                        <img
                            alt="example"
                            src={FakeArticles[0].img}
                        />
                        
                        }
                        
                        actions={[
                          <Icon type="read" key="ellipsis2" />,
                            <Icon type="delete" key="ellipsis" />
                        ]}
                        >
                          
                        <Meta
                          title={FakeArticles[0].title}
                          description={FakeArticles[0].description}
                        />


                  
                      </Card>


                    </div>



       

                

             </div>
      
 

      </div>
  );
}

function mapStateToProps(state) {
  return {wishList: state.wishList}
}

export default connect(mapStateToProps, null) (ScreenMyArticles);
