export default function (wishList=[], action){
    if(action.type === 'addArticle'){
        let wishListCopy = [...wishList];
        for(let i=0; i<wishListCopy.length; i++){
            if(wishListCopy[i].title === action.article.title){
                console.log('deja présent');
            } else {
                console.log('nouveau');
            }
        }
        wishListCopy.push(action.article);
        return wishListCopy;
    } else if (action.type === 'deleteArticle') {
        let wishListCopy = [...wishList];

        wishListCopy = wishListCopy.filter(article => article.title !== action.articleTitle)

        return wishListCopy;
        
    } else {
        return wishList;
    }
}
