import React, { useState, useEffect } from 'react';
import './App.css';
import { Card, Icon, Modal, Button } from 'antd';
import Nav from './Nav'

const { Meta } = Card;

function ScreenArticlesBySource(props) {

  const idSource = props.match.params.id

  const [articleList, setArticleList] = useState([]);
  const [visible, setVisible] = useState(false); // Visibilité de la modal

  useEffect(() => {
    async function loadArticles() {
      // On interroge l'api pour récup la liste des sources
      const rawResponse = await fetch(`https://newsapi.org/v2/top-headlines?sources=${idSource}&apiKey=ab9c4b0f827c443dbc683714a21e9e38`);
      const response = await rawResponse.json();
      setArticleList(response.articles);

    }
    loadArticles();
  }, []);

  let showModal = () => {
    setVisible(!visible);
  }

  let closeModal = () => {
    setVisible(!visible);
  }

  let listArticles = articleList.map((article, i) => {
    return (
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <Card key={i}
          style={{
            width: 300,
            margin: '15px',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between'
          }}
          cover={
            <img
              alt={article.title}
              src={article.urlToImage}
            />
          }
          actions={[
            <Button type="primary" onClick={showModal()}><Icon type="read" key="ellipsis2" /></Button>,
            <Modal
              title={article.title}
              visible={visible}
              onOk={closeModal()}
              onCancel={closeModal()}
            >
              <p>{article.content}</p>
            </Modal>,
            <Icon type="like" key="ellipsis" />
          ]}
        >

          <Meta
            title={article.title}
            description={article.description}
          />

        </Card>
      </div>
    );
  });


  return (
    <div>

      <Nav />

      <div className="Banner" />

      <div className="Card">

        {listArticles}

      </div>

    </div>
  );
}

export default ScreenArticlesBySource;
