export default function (wishList=[], action){
    if(action.type === 'addArticle'){
        let wishListCopy = [...wishList];
        wishListCopy.push(action.article);
        return wishListCopy;
    } else if (action.type === 'deleteArticle') {
        let wishListCopy = [...wishList];
        
        for(let i=0; i< wishListCopy.length; i++){
            if (wishListCopy[i].title === action.articleTitle){
                wishListCopy = wishListCopy.slice(i, 1);
            }
            
        }
        return wishListCopy;
        
    } else {
        return wishList;
    }
}
