import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom';
import './App.css';
import { List, Avatar } from 'antd';
import Nav from './Nav'

function ScreenSource() {



  const [sourceList, setSourceList] = useState([]);

  useEffect(() => {
    async function loadSource() {
      // On interroge l'api pour récup la liste des sources
      const rawResponse = await fetch('https://newsapi.org/v2/sources?language=fr&country=fr&apiKey=ab9c4b0f827c443dbc683714a21e9e38');
      const response = await rawResponse.json();
      setSourceList(response.sources);
      console.log(response.sources);
    }
    loadSource();
  }, [])

  // let listSource = sourceList.map((source, i) => {
  //   return (
  //   <List.Item>
  //     <List.Item.Meta
  //       avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
  //       title={<a href={source.url}>{source.name}</a>}
  //       description={source.description}
  //     />
  //   </List.Item>)
  // })


  return (
    <div>
      <Nav />

      <div className="Banner" />

      <div className="HomeThemes">

        <List
          itemLayout="horizontal"
          dataSource={sourceList}
          renderItem={item => (
                    <List.Item>
                      <List.Item.Meta
                        avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                        title={<Link to={`/screenarticlesbysource/${item.id}`}><h3>{item.name}</h3></Link>}
                        description={item.description}
                      />
                    </List.Item>
                  )}
        />


      </div>

    </div>
  );
}

export default ScreenSource;
