import React, { useState, useEffect } from 'react';
import './App.css';
import { Card, Icon, Modal } from 'antd';
import Nav from './Nav'
import { connect } from 'react-redux';

const { Meta } = Card;

function ScreenMyArticles(props) {

  const [wishList, setWishList] = useState([]);

  const [visible, setVisible] = useState(false); // VisibilitÃ© de la modal
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  useEffect(() => {
    async function loadWishlist() {
      // On interroge l'api pour rÃ©cup la liste des sources
      const rawResponse = await fetch(`/get-wishlist/${props.token}`);
      const response = await rawResponse.json();
      setArticleList(response.articles);

    }
    loadWishlist();
  }, [])


  useEffect(() => {
      setWishList(props.myArticles);
  }, [props.myArticles]);
  

  let showModal = (title, content) => {
    setVisible(!visible);
    setTitle(title);
    setContent(content)
  }

  let closeModal = () => {
    setVisible(!visible);
  }


  let listArticles = wishList.map((article, i) => {
      return (
        <div key={i} style={{ display: 'flex', justifyContent: 'center' }}>
      <Card 
        style={{
          width: 300,
          margin: '15px',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-between'
        }}
        cover={
          <img
            alt={article.title}
            src={article.img}
          />

        }

        actions={[
          <Icon onClick={()=>showModal(article.title, article.content)} type="read" key="ellipsis2" />,
          <Icon onClick={()=>props.deleteToWishList(article.title)} type="delete" key="ellipsis" />
        ]}
      >

        <Meta
          title={article.title}
          description={article.description}
        />



      </Card>
      <Modal 
      title={title}
      visible={visible}
      onOk={closeModal}
      onCancel={closeModal}
    >
      <p>{content}</p>
    </Modal>
    </div>
    )
    
    
  });

  return (
    <div>

      <Nav />

      <div className="Banner" />

      <div className="Card">


        

          {wishList.length >0 ?listArticles:<div style={{marginTop: "30px"}}>Aucun article</div>}
          

        







      </div>



    </div>
  );
}

function mapStateToProps(state) {
  return { 
    myArticles: state.wishList,
    token: state.userToken
  }
}

function mapDispatchToProps(dispatch){
  return {
    deleteToWishList: function(title){
      dispatch( {type: 'deleteArticle', articleTitle: title } )
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ScreenMyArticles);
